import { useState } from "react";
import { MovieCard } from "../movie-card/movie-card";
import { MovieView} from "../movie-view/movie-view";

export const MainView = () => {
    const [selectedMovie, setSelectedMovie] = useState(null);

    const [movies, setMovies] = useState([
        {
            id: 1,
            title: "Agantuk",
            description: "Anila Bose and her family receive a visit from Manmohan, a man claiming to be her long-lost uncle. However, as time passes, they begin to question Manmohan's intentions.",
            image: "data:image/jpeg;base64,",
            genre: "Drama",
            director: "Satyajit Ray"
          },
          {
            id: 2,
            title: "Goopy Gyne Bagha Byne",
            description: "Goopy and Bagha are banished from the kingdom for being bad musicians. However, with their skills, they succeed in pleasing the king of ghosts who grants them three wishes.",
            image: "data:image/jpeg;base64,",
            genre: "Comdey",
            director: "Satyajit Ray"
          },
          {
            id: 3,
            title: "Panther Panchali",
            description: "Apu and his sister Durga try to make the most out of their poverty-stricken lives. Soon, when their father goes to the city for a job and their mother becomes increasingly bitter, things go downhill.",
            image: "data:image/jpeg;base64,",
            genre: "drama",
            director: "Satyajit Ray"
          }
          
    ]);

    if(selectedMovie){
        return (
            <MovieView 
            movie={selectedMovie}
            onBackClick={() => setSelectedMovie(null)} />
        )
    }

    if(movies.length === 0){
        return <div>The list is empty!</div>
    }

    return(
        <div>
            {movies.map((movie) => (
               <MovieCard
                key={movie.id}
                movie={movie}
                onMovieClick={(newSelectedMovie) => {
                    setSelectedMovie(newSelectedMovie);
                }}               

                />
            ))}
        </div>
    );
};
